function render(ctx, notesList, width, height){
    // calculate sizes
    var sizes = getSizes(width, height)
    // draw sheet elements
    drawSheet(ctx, sizes);
    // draw notes
    for (var idx in notesList){
        drawNote(notesList[idx], Number(idx), sizes, ctx)
    }
}


